    private void tfattendancescoreFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_tfattendancescoreFocusLost
        // TODO add your handling code here:
        Double score, equiv, equiv2;
        score = Double.parseDouble(tfattendancescore.getText());
        
        if(score > 20){
            equiv = 20.0 / 20;
            equiv2 = equiv * 10;
            
            tfattendancescore.setText("20");
            tfattendancescoreequivalent.setText(String.valueOf(equiv2 * 10));
            tfattendancescorepercent.setText(String.valueOf(equiv2));
        }
        else{
            equiv = score / 20;
            equiv2 = equiv * 10;
            
            tfattendancescoreequivalent.setText(String.valueOf(equiv2 * 10));
            tfattendancescorepercent.setText(String.valueOf(equiv2));
        }
    }//GEN-LAST:event_tfattendancescoreFocusLost

