    private void jbcomputeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbcomputeActionPerformed
        // TODO add your handling code here:
        Double attendance = Double.parseDouble(tfattendancescorepercent.getText());
        Double written = Double.parseDouble(tfwrittenquizscorepercent.getText());
        Double practical = Double.parseDouble(tfpracticalquizscorepercent.getText());
        Double project = Double.parseDouble(tfprojectscorepercent.getText());
        Double prelim = Double.parseDouble(tfprelimexamscorepercent.getText());
        
        Double total = attendance + written + practical + project + prelim;
        
        tfprelimgrade.setText(String.valueOf(total));
        
        if(total < 75){
            tfremarks.setText("Failed");
        }
        else{
            tfremarks.setText("Passed");
        }
       
    }//GEN-LAST:event_jbcomputeActionPerformed

